import React, { useState } from 'react'; 
import { Container, Row, Col } from 'reactstrap';


const CallToAction = () => {
    
        const [location, setLocation] = useState('');
        const [checkIn, setCheckIn] = useState('');
        const [checkOut, setCheckOut] = useState('');
        const [results, setResults] = useState([]);
      
        const searchAlojamientos = () => {
          // Lógica de búsqueda (ficticia en este ejemplo)
          const fakeResults = [
            { name: 'Hotel 1', price: '$100' },
            { name: 'Hotel 2', price: '$120' },
            { name: 'Apartamento 1', price: '$80' },
          ];
      
          setResults(fakeResults);
        };
      
    return (
        <div className="coming-soon" id="coming">
            <Container className="alojamientos-container">
                <Row>
                    <Col md="6">
                        <div className="d-flex align-items-center">
                        <div className="alojamientos-container">
      <header className="alojamientos-header">
        <h1>Buscador de Alojamientos en Cartagena</h1>
      </header>

      <main className="alojamientos-main">
        <div className="form-container">
          <label htmlFor="location">Ubicación:</label>
          <input
            type="text"
            id="location"
            className="input-field"
            placeholder="Ingrese la ubicación"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />

          <label htmlFor="checkIn">Fecha de entrada:</label>
          <input
            type="date"
            id="checkIn"
            className="input-field"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
          />

          <label htmlFor="checkOut">Fecha de salida:</label>
          <input
            type="date"
            id="checkOut"
            className="input-field"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
          />

          <button className="search-button" onClick={searchAlojamientos}>
            Buscar Alojamientos
          </button>
        </div>

        <div className="results-container">
          <h2>Resultados</h2>
          {results.length > 0 ? (
            <ul className="results-list">
              {results.map((alojamiento, index) => (
                <li key={index}>
                  {alojamiento.name} - {alojamiento.price}
                </li>
              ))}
            </ul>
          ) : (
            <p>No hay resultados</p>
          )}
        </div>
      </main>
    </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default CallToAction;
