import React, { useState } from 'react';
import { Container, Row, Col, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import { Alert } from 'reactstrap';


const RegistrationForm = () => {
  const [firstName, setFirstName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const handleRegistration = (e) => {
    e.preventDefault();
    // Aquí puedes agregar la lógica para enviar la información de registro al servidor
    // y manejar el resultado, por ejemplo, mediante una llamada a una API.

    // Simulación de éxito para propósitos de demostración
    setRegistrationSuccess(true);
  };

  return (
    <div className="static-slider-head">
      <Container>
        <Row className="justify-content-center">
          <Col lg="8" md="6" className="align-self-center text-center">
            <h4 className="title" style={{ marginTop: '1rem' }}>Registrarse </h4>
            <Form onSubmit={handleRegistration} className="row my-4">
              <FormGroup className="col-md-6">
                <Label htmlFor="firstName">Nombre</Label>
                <Input
                  type="text"
                  className="form-control"
                  id="firstName"
                  placeholder="Ingresa tu nombre"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup className="col-md-6">
                <Label htmlFor="email">Correo Electrónico</Label>
                <Input
                  type="email"
                  className="form-control"
                  id="email"
                  placeholder="Ingresa tu correo electrónico"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup className="col-md-6">
                <Label htmlFor="username">Usuario</Label>
                <Input
                  type="text"
                  className="form-control"
                  id="username"
                  placeholder="Ingresa tu usuario"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup className="col-md-6">
                <Label htmlFor="password">Contraseña</Label>
                <Input
                  type="password"
                  className="form-control"
                  id="password"
                  placeholder="Ingresa tu contraseña"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </FormGroup>
              <div className="col-12">
                <Button type="submit" className="btn btn-md m-t-30 btn-info-gradiant font-14">
                  Registrarse
                </Button>
              </div>
            </Form>
            {registrationSuccess && (
              <Alert color="success" className="mt-3">
                ¡Registro exitoso! Ahora puedes iniciar sesión.
              </Alert>
            )}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default RegistrationForm;
