import React, { useState } from 'react';
import { Container, Row, Col, Card, Modal } from 'reactstrap';

const AlojamientosResultados = ({ resultados }) => {
  const [selectedAlojamiento, setSelectedAlojamiento] = useState(null);

  const handleAlojamientoClick = (alojamiento) => {
    setSelectedAlojamiento(alojamiento);
  };

  return (
    <div className="alojamientos-resultados-container">
      <Container>
        <Row>
          {resultados.map((alojamiento) => (
            <Col md="4" key={alojamiento.id}>
              <Card className="alojamiento-card" onClick={() => handleAlojamientoClick(alojamiento)}>
                <img src={alojamiento.imagen} alt={alojamiento.nombre} className="alojamiento-card-image" />
                <div className="alojamiento-card-body">
                  <h5 className="alojamiento-card-title">{alojamiento.nombre}</h5>
                  <p className="alojamiento-card-text">{alojamiento.ubicacion}</p>
                  <p className="alojamiento-card-price">{alojamiento.precio}</p>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {selectedAlojamiento && (
        <Modal isOpen={selectedAlojamiento !== null} toggle={() => setSelectedAlojamiento(null)}>
          <ModalHeader closeButton>
            <ModalTitle>{selectedAlojamiento.nombre}</ModalTitle>
          </ModalHeader>
          <ModalBody>
            <img src={selectedAlojamiento.imagen} alt={selectedAlojamiento.nombre} className="modal-image" />
            <p className="modal-text">
              <strong>Ubicación:</strong> {selectedAlojamiento.ubicacion}
            </p>
            <p className="modal-text">
              <strong>Precio:</strong> {selectedAlojamiento.precio}
            </p>
            <p className="modal-text">
              <strong>Descripción:</strong> {selectedAlojamiento.descripcion}
            </p>
          </ModalBody>
        </Modal>
      )}
    </div>
  );
};

export default AlojamientosResultados;
