import React from 'react';
import Card from './Card';
import Modal from './Modal';

const AlojamientosResultados = ({ resultados }) => {
  const [selectedAlojamiento, setSelectedAlojamiento] = useState(null);

  const handleAlojamientoClick = (alojamiento) => {
    setSelectedAlojamiento(alojamiento);
  };

  return (
    <div className="alojamientos-resultados-container">
      {resultados.length > 0 ? (
        <>
          <h2>Resultados de tu búsqueda</h2>
          <div className="alojamientos-grid">
            {resultados.map((alojamiento) => (
              <Card className="alojamiento-card" key={alojamiento.id} onClick={() => handleAlojamientoClick(alojamiento)}>
                <img src={alojamiento.imagen} alt={alojamiento.nombre} className="alojamiento-card-image" />
                <div className="alojamiento-card-body">
                  <h5 className="alojamiento-card-title">{alojamiento.nombre}</h5>
                  <p className="alojamiento-card-text">{alojamiento.ubicacion}</p>
                  <p className="alojamiento-card-price">{alojamiento.precio}</p>
                  <p className="alojamiento-card-rating">{alojamiento.calificacion} estrellas</p>
                </div>
              </Card>
            ))}
          </div>

          {/* Modal para mostrar la información detallada del alojamiento seleccionado */}
          <Modal isOpen={selectedAlojamiento !== null} onClose={() => setSelectedAlojamiento(null)}>
            <ModalHeader>
              <h5 className="modal-title">{selectedAlojamiento?.nombre}</h5>
            </ModalHeader>
            <ModalBody>
              <p><strong>Ubicación:</strong> {selectedAlojamiento?.ubicacion}</p>
              <p><strong>Precio:</strong> {selectedAlojamiento?.precio}</p>
              <p><strong>Calificación:</strong> {selectedAlojamiento?.calificacion} estrellas</p>
              <p><strong>Descripción:</strong> {selectedAlojamiento?.descripcion}</p>
              {/* ... (Agrega más información del alojamiento) ... */}
            </ModalBody>
          </Modal>
        </>
      ) : (
        <p>No se encontraron alojamientos para tu búsqueda.</p>
      )}
    </div>
  );
};

export default AlojamientosResultados;
